import React, { useState } from 'react';

const Settings = ({ onNavigate }) => {
  const [currentTab, setCurrentTab] = useState('tags'); // tags, admin

  const [tags, setTags] = useState([
    { id: 1, name: '新規', color: '#28a745', description: '新しく追加された友だち', count: 234 },
    { id: 2, name: 'アクティブ', color: '#007bff', description: '最近活動のある友だち', count: 567 },
    { id: 3, name: 'VIP', color: '#ffc107', description: '重要な顧客', count: 45 },
    { id: 4, name: '購入済み', color: '#17a2b8', description: '商品を購入した友だち', count: 189 },
    { id: 5, name: 'リピーター', color: '#6f42c1', description: '複数回購入している友だち', count: 78 }
  ]);

  const [newTag, setNewTag] = useState({ name: '', color: '#007bff', description: '' });
  const [showAddTag, setShowAddTag] = useState(false);

  const [admins, setAdmins] = useState([
    {
      id: 1,
      name: '管理者',
      email: 'admin@example.com',
      role: 'owner',
      status: 'active',
      lastLogin: '2024-01-15 10:30',
      createdAt: '2024-01-01'
    },
    {
      id: 2,
      name: 'スタッフA',
      email: 'staff-a@example.com',
      role: 'editor',
      status: 'active',
      lastLogin: '2024-01-14 16:20',
      createdAt: '2024-01-05'
    },
    {
      id: 3,
      name: 'スタッフB',
      email: 'staff-b@example.com',
      role: 'viewer',
      status: 'inactive',
      lastLogin: '2024-01-10 09:15',
      createdAt: '2024-01-10'
    }
  ]);

  const [inviteForm, setInviteForm] = useState({ email: '', role: 'viewer' });
  const [showInvite, setShowInvite] = useState(false);

  const handleApiSave = () => {
    // API設定の保存処理
    setApiSettings(prev => ({ ...prev, isConnected: true }));
  };

  const handleAddTag = () => {
    if (newTag.name.trim()) {
      const newTagItem = {
        id: Date.now(),
        name: newTag.name,
        color: newTag.color,
        description: newTag.description,
        count: 0
      };
      setTags(prev => [...prev, newTagItem]);
      setNewTag({ name: '', color: '#007bff', description: '' });
      setShowAddTag(false);
    }
  };

  const handleDeleteTag = (tagId) => {
    setTags(prev => prev.filter(tag => tag.id !== tagId));
  };

  const handleInviteAdmin = () => {
    if (inviteForm.email) {
      // 招待メール送信処理
      setInviteForm({ email: '', role: 'viewer' });
      setShowInvite(false);
    }
  };

  const renderAccountSettings = () => (
    <div className="settings-content">
      <div className="card">
        <h2 className="card-title">LINE公式アカウント連携</h2>
        <p className="card-description">
          LINE Developers Consoleで取得したAPIキーを設定してください。
        </p>

        <div className="connection-status">
          <div className={`status-indicator ${apiSettings.isConnected ? 'connected' : 'disconnected'}`}>
            {apiSettings.isConnected ? '✓ 接続済み' : '✗ 未接続'}
          </div>
        </div>

        <div className="form-group">
          <label className="form-label">Channel ID</label>
          <input
            type="text"
            className="form-input"
            value={apiSettings.channelId}
            onChange={(e) => setApiSettings(prev => ({ ...prev, channelId: e.target.value }))}
            placeholder="1234567890"
          />
        </div>

        <div className="form-group">
          <label className="form-label">Channel Secret</label>
          <input
            type="password"
            className="form-input"
            value={apiSettings.channelSecret}
